function yearslist(startYear, endYear){
  var years = []
  for(var i=startYear;i<=endYear;i++){
      years.push(i);
  } 
  return years;
}

function monthslist(startMonth, endMonth){
  var months = []
  for(var i=startMonth;i<=endMonth;i++){
      months.push(i);
  } 
  return months;
}

function urlformatter(inputyear, inputmonth){
  var url = "https://chiefobrienatwork.com/archive/" + (inputyear).toString() + "/" + (inputmonth).toString()
  return url;
}

Agent.check = function() {
  var currentYear = new Date().getFullYear();
  var currentMonth = new Date().getMonth();
  var yearlist = yearslist(2014, currentYear)
  for (yearindex = 0; yearindex < yearlist.length; yearindex++) {
    var indexyear = yearlist[yearindex]
    if (indexyear == 2014) {
      var monthslist = [12]
    } else if (indexyear == currentYear) {
      var monthslist = yearslist(1, currentMonth+1)
    } else {
      var monthslist = yearslist(1, 12)
    }
    for (monthindex = 0; monthindex < monthslist.length; monthindex++) {
      var indexmonth = monthslist[monthindex]
      var dateurl = urlformatter(indexyear, indexmonth)
      this.createEvent(
        {
          'year': indexyear,
          'month': indexmonth,
          'dateurl': dateurl,
        });
    }
  }
};
