function ISODateString(d){
 function pad(n){return n<10 ? '0'+n : n}
 return d.getUTCFullYear()+'-'
      + pad(d.getUTCMonth())+'-'
      + pad(d.getUTCDate())+'T'
      + pad(d.getUTCHours())+':'
      + pad(d.getUTCMinutes())+':'
      + pad(d.getUTCSeconds())+'Z'}

Agent.receive = function() { 
   var events = this.incomingEvents();
   for(var i = 0; i < events.length; i++) {
    var body = events[i].payload['body']
    var summary = events[i].payload['title']
    var germanDate = body.match(/\w+, (\d+). (\w+) (\d+)/)
    var year = events[i].payload['year']
    var month = events[i].payload['month']
    var day = events[i].payload['day']
    var hour_start = events[i].payload['hour_start']
    var hour_end = events[i].payload['hour_end']
    var minute = events[i].payload['minute']
    var startDate = new Date(year, month, day, hour_start, minute, 0, 0)
    var endDate = new Date(year, month, day, hour_end, minute, 0, 0)
    
    this.createEvent(
      { 'message': 
        { 'summary': summary,
          'visibility': 'default',
          'description': body,
          'start': {
           'dateTime': ISODateString(startDate)
          },
          'end': {
            'dateTime': ISODateString(endDate)
          }
        }
      }
    );
  }
}
